import streamlit as st

st.title("Playground Layout")

col1, col2 = st.columns(2)

name = st.sidebar.text_input("Enter Your Name")   

agree = st.sidebar.selectbox("You agreed with T&C for Voting",["None","Yes","No"])   

if name and agree == 'Yes':
    st.sidebar.success(f"Thank you {name} for proceeding for voting")
elif name and agree == 'No':    
    st.sidebar.error(f"No worries {name} you may decide and come for voting later")
else:
    st.sidebar.error(f"Agree before voting")

with col1:
    st.header("iOS")
    st.image("data:image/png;base64,",width=200)
    vote1= st.button("Vote for iOS")

with col2:
    st.header("Andriod")
    st.image("data:image/jpeg;base64,",
          width=200)
    vote2= st.button("Vote for Andriod")    
if (agree == 'Yes') and (vote1 or vote2):
    if vote1:
        st.success("Thank for your trust in iOS")    
    elif vote2:
        st.success("Thank for your trust in Andriod")       
else:
    st.error("Agree to T&C before voting")    

with st.expander("**Term & Conditions**"):
    st.markdown("""
    1. **Eligibility**: Only valid users are allowed to vote.
    2. **One Vote Per Person**: You may vote only once.
    3. **Voting Period**: Open from July 1 to July 15, 2025.
    4. **Data Use**: Your name may be stored to prevent duplicate voting.
    5. **Fair Use**: Misuse will lead to disqualification.
    6. **By voting, you accept these terms.**
    """)
